  | &#039;invalidFirstCharacterOfTagName&#039;
  | &#039;misplacedDoctype&#039;
  | &#039;misplacedStartTagForHeadElement&#039;
  | &#039;missingAttributeValue&#039;
  | &#039;missingDoctype&#039;
  | &#039;missingDoctypeName&#039;
  | &#039;missingDoctypePublicIdentifier&#039;
